'{
  "data": {
    "name": "dataset"
  },
  "params": [],
  "transform": [
    {
      "filter": {
        "field": "calendar_year",
        "oneOf": [
          2022,
          2023
        ]
      }
    },
    {
      "pivot": "calendar_year",
      "value": "sales",
      "groupby": [
        "brand",
        "__selected__"
      ]
    },
    {
      "calculate": "datum[2023] > datum[2022] ? true : false",
      "as": "higher"
    },
    {
      "calculate": "(datum[2023] - datum[2022]) / datum[2022]",
      "as": "change"
    }
  ],
  "encoding": {
    "tooltip": [
      {
        "field": "brand",
        "title": "Brand"
      },
      {
        "field": "2022",
        "format": ",.2~s",
        "title": "PY"
      },
      {
        "field": "2023",
        "format": ",.2~s",
        "title": "CY"
      },
      {
        "field": "change",
        "format": "+.1%",
        "title": "YoY %"
      }
    ],
    "y": {
      "field": "brand",
      "axis": {
        "title": null,
        "labelPadding": 100,
        "labelLimit": 200,
        "grid": true,
        "gridOpacity": 0.15,
        "zindex": 0
      },
      "sort": {
        "field": "2022",
        "order": "ascending"
      }
    },
    "opacity": {
      "condition": {
        "test": {
          "field": "__selected__",
          "equal": "off"
        },
        "value": 0.3
      },
      "value": 1
    }
  },
  "layer": [
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "stroke": "#000",
        "strokeWidth": 1.5,
        "fill": "#a3a3a3",
        "filled": false,
        "xOffset": {
          "expr": "datum.higher ? -1.5 : 1.5"
        }
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "type": "quantitative"
        },
        "color": {
          "value": "#e6959c"
        },
        "size": {
          "value": 350
        },
        "opacity": {
          "condition": [{
            "test": {
              "field": "__selected__",
              "equal": "off"
            },
            "value": 0.0

          },
          {
            "test": {
              "field": "__selected__",
              "equal": "neutral"
            },
            "value": 0.0

          }],
          "value": 1
        }
      }
    },
    {
      "mark": {
        "type": "rule",
        "strokeWidth": 22,
        "strokeCap": "round",
        "stroke": "#000"
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "axis": {
            "format": ",.2~s",
            "grid": true,
            "tickCount": 6,
            "gridDash": [
              5,
              5
            ],
            "gridOpacity": 0.2,
            "ticks": true
          },
          "type": "quantitative"
        },
        "x2": {
          "field": "2023",
          "type": "quantitative"
        },
        "opacity": {
          "condition": [{
            "test": {
              "field": "__selected__",
              "equal": "off"
            },
            "value": 0.0

          },
          {
            "test": {
              "field": "__selected__",
              "equal": "neutral"
            },
            "value": 0.0

          }],
          "value": 1
        }
      }
    },
    {
      "mark": {
        "type": "rule",
        "strokeWidth": 20,
        "strokeCap": "round",
        "stroke": "#e3e3e3"
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "axis": {
            "format": ",.2~s",
            "grid": true,
            "tickCount": 6,
            "gridDash": [
              5,
              5
            ],
            "gridOpacity": 0.2,
            "ticks": true
          },
          "type": "quantitative"
        },
        "x2": {
          "field": "2023",
          "type": "quantitative"
        }
      }
    },
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "stroke": "#e3e3e3",
        "strokeWidth": 2,
        "fill": "#a3a3a3",
        "filled": true
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "type": "quantitative"
        },
        "color": {
          "value": "#e6959c"
        },
        "size": {
          "value": 275
        }
      }
    },
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "shape": {
          "expr": "datum.higher ? ''triangle-right'' : ''triangle-left''"
        },
        "stroke": "black",
        "strokeWidth": 2,
        "filled": false,

        "xOffset": {
          "expr": "datum.higher ? 13 : -13"
        }
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2023",
          "type": "quantitative"
        },
        "size": {
          "value": 275
        },
        "opacity": {
          "condition": [{
            "test": {
              "field": "__selected__",
              "equal": "off"
            },
            "value": 0.0

          },
          {
            "test": {
              "field": "__selected__",
              "equal": "neutral"
            },
            "value": 0.0

          }],
          "value": 1
        }
      }
    },
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "shape": {
          "expr": "datum.higher ? ''triangle-right'' : ''triangle-left''"
        },
        "stroke": "#e3e3e3",
        "strokeWidth": 2,
        "filled": true,
        "fill": {
          "expr": "datum.higher ? ''limegreen'' : ''crimson''"
        },
        "xOffset": {
          "expr": "datum.higher ? 11 : -11"
        }
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2023",
          "type": "quantitative"
        },
        "size": {
          "value": 275
        }
      }
    },
    {
      // New text mark layer to display 2023 sales values
      "mark": {
        "type": "text",
        "align": {
          "expr": "datum.higher ? ''left'' : ''right''"
        }, // Align the text to the left of the point
        "baseline": "middle",
        "dx": {
          "expr": "datum.higher ? 30 : -30"
        }, // Offset the text to avoid overlapping the point
        "dy": 0, // Slight vertical adjustment
        "fontSize": 18, // Set the font size
        "fontWeight": "bold", // Make the text bold for emphasis
        "color": {
          "expr": "datum.higher ? ''limegreen'' : ''crimson''"
        } // Set the text color to match the point
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2023",
          "type": "quantitative"
        },
        "text": {
          "field": "change", // Display the 2023 sales value
          "type": "quantitative",
          "format": "+0.0%"
        }
      }
    }
  ]
}'

