'{
  "data": {
    "name": "dataset"
  },
  "transform": [
    // Filter the data to include only the years 2022 and 2023
    {
      "filter": {
        "field": "calendar_year",
        "oneOf": [2022, 2023]
      }
    },
    // Pivot the data so that sales values for 2022 and 2023 become separate fields
    {
      "pivot": "calendar_year",
      "value": "sales",
      "groupby": ["brand"]
    },
    // Calculate whether 2023 sales are higher than 2022 sales
    {
      "calculate": "datum[2023] > datum[2022] ? true : false",
      "as": "higher" // Store this calculation result in a new field called ''higher''
    }
  ],
  "encoding": {
    // Encoding for the x-axis, where ''brand'' is displayed, sorted by 2023 sales in ascending order
    "x": {
      "field": "brand",
      "axis": {
        "grid": true,
        "gridOpacity": 0.3,
        "zindex": 0
      },
      "sort": {
        "field": "2023",
        "order": "ascending"
      }
    }
  },
  "layer": [
    {
      // Line mark to connect 2022 and 2023 sales for each brand
      "mark": {
        "type": "line",
        "width": 7,
        "strokeWidth": 10,
        // Apply conditional formatting to the line stroke based on whether 2023 sales are higher or lower than 2022
        "stroke": {
          "expr": "datum.higher ? ''limegreen'' : ''crimson''"
        }
      },
      "encoding": {
        // Encoding for the x-axis, displaying the ''brand''
        "x": {
          "field": "brand"
        },
        // Encoding for the y-axis, starting from the 2022 sales value
        "y": {
          "field": "2022",
          "axis": {
            "format": ",.2~s",
            "grid": true,
            "tickCount": 6,
            "gridDash": [5, 5],
            "gridOpacity": 0.2,
            "ticks": true
          },
          "type": "quantitative"
        },
        // Encoding for the y2 position, which ends at the 2023 sales value
        "y2": {
          "field": "2023",
          "type": "quantitative"
        }
      }
    },
    {
      // First point mark for the 2022 sales values
      "mark": {
        "type": "point",
        "tooltip": true,
        "stroke": "#e3e3e3",
        "fill": "darkgrey",
        "filled": true
      },
      "encoding": {
        "x": {
          "field": "brand"
        },
        "y": {
          "field": "2022",
          "type": "quantitative"
        },
        "color": {
          "value": "#e6959c"
        },
        "size": {
          "value": 100
        },
        "opacity": {
          "value": 1
        }
      }
    },
    {
      // Second point mark for the 2023 sales values, with shape and fill color based on whether 2023 sales are higher or lower
      "mark": {
        "type": "point",
        "tooltip": true,
        "shape": {
          "expr": "datum.higher ? ''triangle-up'' : ''triangle-down''"
        },
        "filled": true,
        "fill": {
          "expr": "datum.higher ? ''limegreen'' : ''crimson''"
        },
        "yOffset": {
          "expr": "datum.higher ? -4 : 4"
        }
      },
      "encoding": {
        "x": {
          "field": "brand"
        },
        "y": {
          "field": "2023",
          "type": "quantitative"
        },
        "size": {
          "value": 100
        },
        "opacity": {
          "value": 1
        }
      }
    }
  ]
}'

