'{
  "data": {
    "name": "dataset"
  },
  "transform": [
    {
      "filter": {
        "field": "calendar_year",
        "oneOf": [2022, 2023]
      }
    },
    {
      "pivot": "calendar_year",
      "value": "sales",
      "groupby": ["brand"]
    },
    {
      "calculate": "datum[2023] > datum[2022] ? true : false",
      "as": "higher"
    }
  ],
  "encoding": {
    "y": {
      "field": "brand",
      "axis": {
        "grid": true,
        "gridOpacity": 0.3,
        "zindex": 0
      },
      "sort": {
        "field": "2023",
        "order": "ascending"
      }
    }
  },
  "layer": [
    {
      "mark": {
        "type": "line",
        "height": 7,
        "strokeWidth": 10,
        "stroke": "#e3e3e3"
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "axis": {
            "format": ",.2~s",
            "grid": true,
            "tickCount": 6,
            "gridDash": [5,5],
            "gridOpacity": 0.2,
            "ticks": true
          },
          "type": "quantitative"
        },
        "x2": {
          "field": "2023",
          "type": "quantitative"
        },
        "color": {
          "value": "#e3e3e3"
        }
      }
    },
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "stroke": "#e3e3e3",
        "fill": "darkgrey",
        "filled": true
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2022",
          "type": "quantitative"
        },
        "color": {
          "value": "#e6959c"
        },
        "size": {
          "value": 100
        },
        "opacity": {
          "value": 1
        }
      }
    },
    {
      "mark": {
        "type": "point",
        "tooltip": true,
        "shape": {
          "expr": "datum.higher ? ''triangle-right'' : ''triangle-left''"
        },
        "filled": true,
        "fill": {
          "expr": "datum.higher ? ''limegreen'' : ''crimson''"
        },
        "xOffset": {
          "expr": "datum.higher ? 4 : -4"
        }
      },
      "encoding": {
        "y": {
          "field": "brand"
        },
        "x": {
          "field": "2023",
          "type": "quantitative"
        },
        "size": {
          "value": 100
        },
        "opacity": {
          "value": 1
        }
      }
    }
  ]
}
'

